import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import java.text.SimpleDateFormat

// Get the flowfile
def flowFile = session.get()
if (flowFile != null) {
    // Create a ByteArrayOutputStream to capture the flowfile content
    def byteArrayOutputStream = new ByteArrayOutputStream()
    session.read(flowFile, { inputStream ->
        byte[] buffer = new byte[1024]
        int len
        while ((len = inputStream.read(buffer)) != -1) {
            byteArrayOutputStream.write(buffer, 0, len)
        }
    } as InputStreamCallback)

    // Convert byteArrayOutputStream to String
    def content = byteArrayOutputStream.toString("UTF-8")

    // Parse JSON content
    def jsonSlurper = new JsonSlurper()
    def data = jsonSlurper.parseText(content)

    // Split the data based on timestamp and group together price, market_cap, and volume
    def groupedData = []

    data.prices.eachWithIndex { price, index ->
        def timestamp = price.timestamp

        def marketCap = data.market_caps.find { it.timestamp == timestamp }?.market_cap
        def volume = data.total_volumes.find { it.timestamp == timestamp }?.volume

        if (marketCap != null && volume != null) {
            groupedData.add([
                timestamp: timestamp,
                price: price.price,
                market_cap: marketCap,
                volume: volume
            ])
        }
    }

    // Debugging to check the new grouped data
    log.info("Grouped Data: " + groupedData)

    // Convert back to JSON string
    def newContent = JsonOutput.toJson(groupedData)

    // Write back to flow file
    flowFile = session.write(flowFile, { outputStream ->
        outputStream.write(newContent.getBytes("UTF-8"))
    } as OutputStreamCallback)

    session.transfer(flowFile, REL_SUCCESS)
}
